void setup() {
  Serial.begin(9600); // Initialize the Serial port
  randomSeed(analogRead(A0)); // Initialize random seed for picking word
  pinMode(LED, HIGH); // Set the LED pin to output
  tft.initR(INITR_144GREENTAB);
  tft.fillScreen(ST7735_WHITE);
  tft.setRotation(1);
  drawGrid();
  tft.setFont(&FreeMonoBold9pt7b);
  tft.setTextColor(ST7735_BLACK, ST7735_WHITE);
  tft.setCursor(4, 14);
  tft.println("W");
  tft.setCursor(4, 31);
  tft.println("O");
  tft.setCursor(4, 48);
  tft.println("R");
  tft.setCursor(4, 65);
  tft.println("D");
  tft.setCursor(4, 82);
  tft.println("L");
  tft.setCursor(4, 99);
  tft.println("E");

  while(!Serial) {} // Wait for Serial port to be ready

  pickWord(); // Pick random word

  
}

void loop() {

  if (Serial.available() > 0) {
    char input = byte(Serial.read()); // Get a single keyboard entry

    // Print letter if less than 5 are present, but not enter key.
    // Only allow letters.
    if (letter_count < 5 && isAlpha(input) && input != '\n') {
      char letter = input & ~(0x20); // Convert letter to uppercase
      Serial.print(letter);
      guess[letter_count] = letter;

      letter_count++;
      digitalWrite(LED, HIGH);
      delay(50);
      digitalWrite(LED, LOW);
    }
    
    // Enter key submits guess if 5 letters are present.
    if (letter_count == 5 && input == '\n') {
      letter_count = 0;
      Serial.println();
      Serial.println("######");
      Serial.println(the_word);
      Serial.println();
      Serial.println(guess);
      Serial.println("######");
      if (turn < turn_max && strcmp(the_word, guess) == 0) { // Win
        win = true;
        Serial.println("You won!");
      }
      else { // Check letters
        for (size_t i = 0; i < sizeof(guess); i++) {
            char * pch;
            pch = strchr(the_word, guess[i]);
            if (pch != NULL) {
              Serial.println(guess[i]);
              Serial.println(pch - the_word);
            }
        }
      }
      if (turn == turn_max) { // Lose
        Serial.println(the_word);
      }
      turn++;
    }

  }

}